.container {
	&-body {
		width: 100%;
		min-width: 1160px;

		&-row {
			width: 1160px;
			margin: 0 auto;

			&-title {
				padding: 72px 0 32px;
				display: -webkit-box;
				display: flex;
				-webkit-box-align: end;
				align-items: flex-end;
				-webkit-box-pack: justify;
				justify-content: space-between;

				h2 {
					font-weight: 600;
					font-size: 28px;
					line-height: 36px;
					color: #212121;
				}
			}

			&-list {
				display: flex;
				gap: 19px;
				overflow: unset;

				.flex-wrap {
					flex-wrap: wrap;
				}

				.slide-noi-tieng-item {
					display: flex;
					gap: 20px;
				}

				.swiper-slide {
					opacity: 0;
				}

				.swiper-slide-active {
					opacity: 1;
				}

				.height-fit-content {
					height: fit-content;
					border: 1px solid #e0e0e0;
					padding: 20px;
				}

				&-item {
					width: 374px;
					height: 160px;
					position: relative;
					display: inline-block;
					vertical-align: middle;
					-webkit-transition: all 0.2s ease;
					transition: all 0.2s ease;
					border-radius: 8px;
					cursor: pointer;

					&-link {
						width: 374px;
						border-radius: 8px;

						&-img {
							display: flex;
							-webkit-box-align: center;
							align-items: center;
							-webkit-box-pack: center;
							justify-content: center;

							img {
								width: 230px;
								height: 166px;
							}
						}

						&-sub {
							h3 {
								font-size: 24px;
								line-height: 1.18;
								font-weight: 600;
								margin-top: 23px;
								text-align: center;
								color: #212121;
								overflow: hidden;
								text-overflow: ellipsis;
								display: -webkit-box;
								-webkit-box-orient: vertical;
							}

							p {
								font-size: 14px;
								line-height: 1.44;
								font-weight: 400;
								text-align: center;
								color: #212121;
								margin-top: 8px;
								overflow: hidden;
								text-overflow: ellipsis;
								-webkit-line-clamp: 2;
								display: -webkit-box;
								-webkit-box-orient: vertical;
							}
						}

						&-button {
							margin-top: 12px;
							display: -webkit-box;
							display: flex;
							-webkit-box-pack: center;
							justify-content: center;

							button {
								min-width: 72px;
								line-height: 20px;
								font-size: 14px;
								padding: 7px 16px;
								color: #212121;
								background-color: transparent;
								border: 1px solid #4a4a4a;
								display: inline-block;
								font-stretch: normal;
								font-style: normal;
								font-weight: 500;
								border-radius: 6px;
								margin: 0;
								text-align: center;
								outline: none;
								text-transform: none;
								text-decoration: none;
								overflow: hidden;
								cursor: pointer;
							}
						}
					}

					&-text {
						display: flex;
						overflow: hidden;
						flex-direction: column;
						position: absolute;
						bottom: 20px;
						width: 100%;
						padding: 0px 20px;
						margin: 0px;
						-webkit-box-orient: vertical;
						-webkit-box-direction: normal;

						span {
							display: -webkit-box;
							font-size: 20px;
							line-height: 26px;
							color: rgb(255, 255, 255);
							font-weight: 500;
							max-height: 78px;
							overflow: hidden;
							text-overflow: ellipsis;
							word-break: break-word;
							-webkit-box-orient: vertical;
							-webkit-box-align: start;
							-webkit-line-clamp: 3;
						}
					}

					a {
						width: 100%;
						height: 100%;
					}

					&-img {
						width: 100%;
						height: 100%;
						background-size: cover;
						vertical-align: top;
						border-radius: 8px;
					}

					@include hoverItem(-4px, 0 4px 20px rgb(0 0 0 / 10%), 2);

					&-content {
						&-top {
							display: flex;
							overflow: hidden;
							flex-direction: column;
							background-color: rgb(255, 255, 255);
