$primaryColor: #ff5b00;
$whiteBackground: #ffffff;
$backgroundColor: #fafafa;

$primaryBorder: 1px solid #e0e0e0;

$textBlack: #212121;
$textBlue: #437dff;
$textBlackLight: #333333;
$textGray: #757575;
$textGrayLight: #999999;
$textOrange: #ff5b00;
$textWhite: #ffffff;


$primaryButtonColor: #ff5b00;
$primaryHoverButtonColor: #e05000;


$primaryBackgroundColor: #f5f5f5;
$primaryHoverBackgroundColor: #eeeeee;

$primaryBoxShadow: 0 6px 12px 0 rgb(0 0 0 / 12%), 0 -1px 2px 0 rgb(0 0 0 / 8%);


$primaryButtonColor: #ff5b00;
$primaryHoverButtonColor: #e05000;


$primaryBackgroundColor: #f5f5f5;
$primaryHoverBackgroundColor: #eeeeee;

$boxShadow: 0 4px 20px 0 rgb(0 0 0 / 8%);
$primaryBoxShadow: 0 6px 12px 0 rgb(0 0 0 / 12%), 0 -1px 2px 0 rgb(0 0 0 / 8%);

input[type="number"]::-webkit-inner-spin-button,
input[type="number"]::-webkit-outer-spin-button {
  -webkit-appearance: none;
  -moz-appearance: none;
  appearance: none;
  margin: 0;
}

input[type="number"] {
  -moz-appearance: textfield;
}

.hotel-title {
  width: 1160px;
  margin: 0 auto;
  display: flex;
  padding: 40px 0 24px;

  &-content {
    font-size: 24px;
    line-height: 1.18;
    font-weight: 600;
    position: relative;
    padding: 0;
    padding-left: 24px;
    -webkit-box-flex: 1;
    flex-grow: 1;
    color: #212121;
    margin: 0;

    &::before {
      margin-top: 4px;
      display: inline-block;
      content: "";
      position: absolute;
      left: 0;
      top: 0;
      width: 6px;
      height: 24px;
      background-color: #ff5b00;
      border-radius: 4px;
      margin-right: 16px;
    }
  }
}

.line-sticky {
  width: 1160px;
  margin: 0 auto;
  position: relative;
  z-index: 1;
  display: flex;
  transition-property: transform;
  box-sizing: content-box;
  padding: 16px 0;

  &-btn{
    display: flex;
    -webkit-box-align: center;
    align-items: center;
    height: 36px;
    padding: 0 16px;
    font-size: 14px;
    line-height: 1.44;
    color: #212121;
    margin-right: 12px;
    font-weight: normal;
    max-width: 378.667px;
    cursor: pointer;
    border-radius: 6px;
    border: 1px solid #e0e0e0;
    background: none;
 
    &:focus{
      background-color: $textOrange;
      color: $textWhite;
    }
  }

}

.btn{
  display: flex;
  -webkit-box-align: center;
  align-items: center;
  justify-content: center;
  height: 36px;
  padding: 0 16px;
  font-size: 14px;
  line-height: 1.44;
  color: #212121;
  margin-right: 12px;
  font-weight: normal;
  max-width: 378.667px;
  cursor: pointer;
  border-radius: 6px;
  border: 1px solid #e0e0e0;
  background: none;

  &:focus{
    background-color: $textOrange;
    color: $textWhite;
  }
}
