@charset "UTF-8";
input[type=number]::-webkit-inner-spin-button,
input[type=number]::-webkit-outer-spin-button {
  -webkit-appearance: none;
  -moz-appearance: none;
  appearance: none;
  margin: 0;
}

input[type=number] {
  -moz-appearance: textfield;
}

.hotel-title {
  width: 1160px;
  margin: 0 auto;
  display: flex;
  padding: 40px 0 24px;
}
.hotel-title-content {
  font-size: 24px;
  line-height: 1.18;
  font-weight: 600;
  position: relative;
  padding: 0;
  padding-left: 24px;
  -webkit-box-flex: 1;
  flex-grow: 1;
  color: #212121;
  margin: 0;
}
.hotel-title-content::before {
  margin-top: 4px;
  display: inline-block;
  content: "";
  position: absolute;
  left: 0;
  top: 0;
  width: 6px;
  height: 24px;
  background-color: #ff5b00;
  border-radius: 4px;
  margin-right: 16px;
}

.line-sticky {
  width: 1160px;
  margin: 0 auto;
  position: relative;
  z-index: 1;
  display: flex;
  transition-property: transform;
  box-sizing: content-box;
  padding: 16px 0;
}
.line-sticky-btn {
  display: flex;
  -webkit-box-align: center;
  align-items: center;
  height: 36px;
  padding: 0 16px;
  font-size: 14px;
  line-height: 1.44;
  color: #212121;
  margin-right: 12px;
  font-weight: normal;
  max-width: 378.667px;
  cursor: pointer;
  border-radius: 6px;
  border: 1px solid #e0e0e0;
  background: none;
}
.line-sticky-btn:focus {
  background-color: #ff5b00;
  color: #ffffff;
}

.btn {
  display: flex;
  -webkit-box-align: center;
  align-items: center;
  justify-content: center;
  height: 36px;
  padding: 0 16px;
  font-size: 14px;
  line-height: 1.44;
  color: #212121;
  margin-right: 12px;
  font-weight: normal;
  max-width: 378.667px;
  cursor: pointer;
  border-radius: 6px;
  border: 1px solid #e0e0e0;
  background: none;
}
.btn:focus {
  background-color: #ff5b00;
  color: #ffffff;
}

* {
  box-sizing: border-box;
  padding: 0;
  margin: 0;
  font-size: 20px;
  font-family: "Segoe UI", Tahoma, Geneva, Verdana, sans-serif;
}

.round {
  border-radius: 50px;
}

.round-8 {
  border-radius: 8px;
}

.round-4 {
  border-radius: 4px;
}

.display-flex-column {
  display: flex;
  justify-content: center;
  flex-direction: column;
}

.margin-top-40 {
  margin-top: 40px;
}

.filter-gray-100 {
  filter: grayscale(100%);
  -webkit-filter: grayscale(100%);
}

.filter-gray-100:hover {
  filter: grayscale(0);
}

/* Go from zero to full opacity */
@keyframes fadeEffect {
  from {
    opacity: 0;
  }
  to {
    opacity: 1;
  }
}
