.pay {
	margin-top: 60px;
	.path {
		height: 100px;
		background-color: #fff;
		padding: 20px 0;
		.container {
			display: flex;
			justify-content: center;
			align-items: flex-start;
			height: 100%;
		}
		&-item {
			flex: 1;
			text-align: center;
			display: flex;
			align-items: center;
			justify-content: center;
			flex-direction: column;
			height: 100%;
			&-icon {
				width: 20px;
				height: 20px;
				border-radius: 50px;
				overflow: hidden;
				background-color: $primaryBackgroundColor;
				display: flex;
				justify-content: center;
				align-items: center;
				svg {
					width: 20px;
					height: 20px;
				}
			}
			&:nth-of-type(1) {
				svg {
					color: #00b371;
				}
			}
			&:nth-of-type(3) {
				svg {
					color: $primaryColor;
				}
			}
			&-text {
				font-size: 14px;
				font-weight: 400;
				line-height: 20px;
				margin-top: 8px;
				flex: 1;
			}
		}
		&-line {
			width: 200px;
			height: 2px;
			background-color: #00b371;
			margin-top: 10px;
			flex: 2;
		}
	}
	.content {
		padding: 60px 0;
		.container {
			margin-top: 40px;
			display: flex;
			justify-content: flex-start;
			align-items: flex-start;
		}
		&-left {
			border-radius: 8px;
			flex: 2.5;
			overflow: hidden;
			&-heading {
				background-color: #fff;
				display: flex;
				justify-content: space-between;
				align-items: center;
				padding: 17px 24px;
				border: $primaryBorder;
				span:first-child {
					flex: 1;
					display: block;
					font-size: 20px;
					font-weight: 700;
					line-height: 26px;
					color: $primaryColor;
					// overflow: hidden;
				}
			}
			&-content {
				padding: 32px 24px;
				background-color: #fff;
				&-item {
					padding-bottom: 32px;
					&-heading {
						font-size: 20px;
						font-weight: 700;
						line-height: 30px;
					}
					&-content {
						margin-top: 16px;
						&-item {
							padding: 24px;
							margin-bottom: 24px;
							border: $primaryBorder;
							border-radius: 4px;
							display: flex;
							justify-content: flex-start;
							align-items: flex-start;
							&-img {
								width: 100px;
								margin-right: 16px;
								img {
									width: 100%;
									object-fit: contain;
									border-radius: 6px;
								}
							}
							&-content {
								display: flex;
								align-items: flex-start;
								justify-content: flex-start;
								flex-direction: column;
								span {
									&:first-child {
										font-size: 18px;
										line-height: 24px;
										font-weight: 700;
									}
									&:last-child {
										font-size: 14px;
										line-height: 17px;
									}
								}
							}
						}
						.customer-info {
							&-heading {
								font-size: 18px;
								line-height: 24px;
								font-weight: 700;
								margin-bottom: 12px;
							}
							&-content {
								&-row {
									display: flex;
									justify-content: center;
									align-items: center;
									&-item {
										flex: 1;
										margin: 0 20px 24px;
										display: flex;
										align-items: flex-start;
										justify-content: center;
										flex-direction: column;
										span {
											width: 100%;
											font-size: 14px;
											line-height: 20px;
											margin-bottom: 4px;
											input,
											select {
												width: 100%;
												border: none;
												outline: none;
												font-size: 14px;
												line-height: 20px;
											}
											&:last-child {
												padding: 12px;
												border: $primaryBorder;
												border-radius: 6px;
											}
										}
									}
								}
							}
						}
						.upgrade {
							border: $primaryBorder;
							border-radius: 8px;
							&-heading {
								font-size: 18px;
								line-height: 24px;
								font-weight: 700;
								color: $primaryColor;
								padding: 16px 24px;
								background-color: #fff3e0;
							}
							&-content {
								padding: 22px 22px 16px;
								&-item {
									margin-top: 24px;
									padding-bottom: 14px;
									border-bottom: $primaryBorder;
									&-info {
										display: flex;
										justify-content: flex-start;
										align-items: center;
										&-img {
											width: 80px;
											height: 80px;
											margin-right: 16px;
											img {
												width: 100%;
												height: 100%;
												border-radius: 8px;
											}
										}
										&-content {
											display: flex;
											align-items: flex-start;
											justify-content: flex-start;
											flex-direction: column;
											span {
												margin-bottom: 8px;
												font-size: 14px;
												line-height: 20px;
												color: $textGray;
												&:nth-child(1) {
													font-weight: 700;
													color: $textBlack;
												}
											}
										}
									}
									&-plus {
										margin-top: 16px;

										&-display {
											display: flex;
											justify-content: flex-start;
											align-items: center;
											input {
												cursor: pointer;
												height: 20px;
												width: 20px;
												margin-right: 10px;
												margin: 0 10px 8px 0;
											}
											span {
												margin: 0 10px 8px 0;
												&:first-of-type {
													font-size: 18px;
													line-height: 24px;
													font-weight: 700;
												}
												&:nth-of-type(2) {
													font-size: 14px;
													line-height: 20px;
													color: #109d6d;
													padding: 2px 8px;
													background-color: #e9f8f1;
												}
												&:last-of-type {
													cursor: pointer;
												}
											}
										}
										&-hidden {
											padding-left: 24px;
											margin-bottom: 8px;
											display: flex;
											justify-content: center;
											align-items: center;
											display: none;
											&-icon {
												width: 32px;
												height: 32px;
												margin-right: 16px;
												img {
													width: 100%;
													height: 100%;
													object-fit: contain;
													object-position: center;
												}
											}
											&-content {
												flex: 1;
												display: flex;
												align-items: flex-start;
												justify-content: flex-start;
												flex-direction: column;
												span {
													font-size: 16px;
													line-height: 22px;
													a {
														font-size: 14px;
														line-height: 16px;
														text-decoration: underline;
														color: $textGray;
														margin-right: 24px;
													}
												}
											}
										}
									}
									&-refund {
										margin-top: 8px;
										padding: 4px 10px;
										display: flex;
										align-items: center;
										justify-content: center;
										background-color: #fff0e5;
										border-radius: 4px;
										&-icon {
											margin-right: 16px;
											width: 32px;
											height: 32px;
											svg {
												width: 100%;
												height: 100%;
											}
										}
										&-text {
											flex: 1;
											font-size: 12px;
											line-height: 17px;
											color: $primaryColor;
										}
									}
								}
							}
						}
						.coupon {
							margin: 32px 0;
							&-discount {
								> span {
									display: flex;
									justify-content: flex-start;
									align-items: center;
									&:first-of-type {
										cursor: pointer;
									}
									&:last-of-type {
										color: $primaryColor;
										font-size: 14px;
										line-height: 16px;
										margin: 10px 0;
										font-weight: 500;
									}
									> span {
										font-size: 16px;
										line-height: 21px;
										&:nth-of-type(2) {
											font-weight: 700;
											color: $primaryColor;
											margin: 0 16px;
										}
										&:nth-of-type(3) {
											font-size: 24px;
										}
									}
								}
							}
							&-input {
								background-color: $primaryBackgroundColor;
								border-radius: 6px;
								padding: 24px;
								display: none;
								&-in {
									p {
										font-size: 14px;
										line-height: 16px;
										// color: $textGray;
										margin-bottom: 12px;
									}
									> span {
										display: flex;
										justify-content: flex-start;
										align-items: center;
										> span {
											padding: 10px 20px;
											font-size: 16px;
											line-height: 22px;
											border-radius: 6px;
											border: $primaryBorder;
											margin-right: 16px;
											input {
												font-size: 14px;
												line-height: 20px;
												border: none;
												outline: none;
												width: 350px;
											}
											&:first-of-type {
												background-color: #fff;
											}
											&:last-of-type {
												background-color: $primaryButtonColor;
												color: $textWhite;
												font-weight: 600;
												cursor: pointer;
												&:hover {
													background-color: $primaryHoverButtonColor;
												}
											}
										}
									}
								}
								&-recommended {
									max-height: 360px;
									overflow: hidden auto;
									margin-top: 22px;
									&-heading {
										font-size: 14px;
										line-height: 16px;
										color: $textGray;
										margin-top: 24px;
									}
									&-row {
										margin-top: 32px;
										display: flex;
										justify-content: center;
										align-items: center;
										&-item {
											background-color: #fff;
											cursor: pointer;
											flex: 1;
											max-width: 50%;
											padding: 20px;
											border: 1px solid #a3a0a0;
											border-radius: 4px;
											margin: 0 12px;
											&-heading {
												font-size: 16px;
												line-height: 24px;
												font-weight: 700;
												color: $primaryColor;
											}
											&-content {
												> span {
													margin-top: 8px;
												}
												span {
													display: block;
													font-size: 12px;
													line-height: 14px;
													display: flex;
													justify-content: flex-start;
													align-items: flex-start;
													color: $textGray;
													a {
														font-size: 12px;
														line-height: 14px;
														text-decoration: underline;
													}
												}
											}
										}
									}
								}
							}
							&-confirm {
								font-size: 14px;
								line-height: 20px;
								padding: 12px 16px;
								background-color: #fff7e5;
								border: 1px solid #ff9c004d;
								border-radius: 4px;
								margin: 24px 0;
							}
							&-next {
								display: flex;
								justify-content: center;
								align-items: center;
								span {
									flex: 1;
									font-size: 14px;
									line-height: 21px;
								}
								a {
									padding: 10px 20px;
									background-color: $primaryButtonColor;
									font-size: 16px;
									line-height: 22px;
									font-weight: 700;
									color: $textWhite;
									cursor: pointer;
									border-radius: 6px;
									&:hover {
										background-color: $primaryHoverButtonColor;
									}
								}
							}
						}
					}
				}
			}
		}
		&-right {
			margin-left: 24px;
			flex: 1;
			&-items {
				&-item {
					padding: 20px;
					margin-bottom: 20px;
					background-color: #fff;
					border-radius: 6px;
					&-content,
					&-subtotal,
					&-heading {
						padding: 16px 0;
						border-bottom: $primaryBorder;
						> span {
							display: block;
							display: flex;
							justify-content: space-between;
							align-items: center;
							margin-bottom: 8px;
							span {
								&:last-child {
									color: $textBlack;
								}
							}
						}
					}
					&-subtotal {
						border: none;
						padding-bottom: 0;
						> span {
							span {
								&:last-child {
									font-weight: 700;
									font-size: 16px;
									line-height: 21px;
								}
							}
						}
					}
					* {
						font-size: 14px;
						line-height: 18px;
						color: $textGray;
					}
					&-heading {
						span {
							&:first-child {
								font-size: 16px;
								line-height: 22px;
								font-weight: 700;
								color: $textBlack;
							}
						}
					}
				}
			}
			&-total {
				padding: 20px;
				margin-bottom: 20px;
				background-color: #fff;
				border-radius: 6px;
				span {
					font-size: 14px;
					line-height: 20px;
					color: $textGray;
				}
				> span {
					> span {
						padding: 16px 0;
						display: flex;
						justify-content: space-between;
						align-items: center;
						> span {
							&:last-child {
								color: $textBlack;
							}
						}
						&:last-child {
							> span {
								&:last-child {
									color: $primaryColor;
									font-size: 20px;
									font-weight: 700;
								}
							}
						}
					}
					&:last-child {
						display: flex;
						justify-content: center;
						align-items: center;
						border-top: $primaryBorder;
						span {
							&:first-child {
								color: $textBlack;
								flex: 1;
							}
							&:last-child {
								padding: 0;
								width: 43px;
								height: 43px;
								margin-left: 16px;
								svg {
									width: 100%;
									height: 100%;
								}
							}
						}
					}
				}
			}
		}
	}
	&-info {
		.content {
			&-left {
				&-heading {
					span {
						color: #00b371 !important;
						&:last-child {
							cursor: pointer;
						}
					}
					background-color: #f0fffa;
				}
				&-content {
					display: none !important;
				}
			}
		}
	}
	&-complete {
		margin-top: 24px;
		border-radius: 8px;
		overflow: hidden;
		.content-left-content {
			&-promise {
				padding: 16px;
				background-color: #edf5ff;
				color: #437dff;
				display: flex;
				justify-content: center;
				align-items: center;
				span {
					&:first-child {
						margin-right: 16px;
						display: flex;
						justify-content: center;
						align-items: center;
						svg {
							height: 25px;
							width: 25px;
						}
					}
					&:last-child {
						flex: 1;
						font-size: 14px;
						line-height: 18px;
					}
				}
			}
			&-radio {
				&-item {
					position: relative;
					padding: 16px 0;
					display: flex;
					justify-content: space-between;
					align-items: center;
					border-bottom: $primaryBorder;
					span {
						display: inline-block;
					}
					&-text {
						position: relative;
						display: flex;
						justify-content: flex-start;
						flex: 1;
						align-items: center;
						> span {
							font-size: 16px;
							line-height: 20px;
							font-weight: 500;
							&:first-child {
								width: 20px;
								height: 20px;
								border-radius: 50px;
								border: $primaryBorder;
								background-color: #fff;
								margin-right: 16px;
								cursor: pointer;
							}
						}
						.checked {
							border-color: $primaryColor !important;
							&::after {
								content: '';
								width: 14px;
								height: 14px;
								border-radius: 50px;
								background-color: $primaryColor;
								position: absolute;
								top: 3px;
								left: 3px;
							}
						}
					}
					&-icon {
						display: flex;
						justify-content: center;
						align-items: center;
						.momo {
							width: 70px;
							height: 42px;
							background-image: url('../../assets/images/payment.png');
							background-position: 0px -2283px;
						}
						.momoatm {
							background-position: 0px -1813px;
							width: 70px;
							height: 42px;
							background-image: url('../../assets/images/payment.png');
						}
						.paypal {
							background-image: url('../../assets/images/payment.png');
							background-position: 0px -2565px;
							width: 70px;
							height: 42px;
						}
						.credit {
						}
					}
					&-drop {
						margin-top: 10px;
						padding: 24px 24px 36px;
						background-color: red !important;
					}
				}
			}
		}
	}

	&-total {
		margin-top: 24px;
		border-radius: 8px;
		overflow: hidden;
		.content-left-content {
			display: flex;
			justify-content: center;
			align-items: center;
			> span {
				&:first-child {
					flex: 1;
					font-size: 14px;
					line-height: 21px;
					a {
						font-size: 14px;
						line-height: 21px;
						text-decoration: underline;
					}
				}
				&:last-child {
					display: flex;
					align-items: center;
					justify-content: center;
					flex-direction: column;
					margin-left: 40px;
					> span {
						&:first-child {
							font-size: 20px;
							line-height: 21px;
							font-weight: 700;
							color: $primaryColor;
							margin-bottom: 12px;
						}
						&:last-child {
							a {
								font-size: 16px;
								line-height: 22px;
								font-weight: 700;
								color: $textWhite;
								padding: 10px 20px;
								background-color: $primaryButtonColor;
								border-radius: 6px;
								&:hover {
									background-color: $primaryHoverButtonColor;
								}
							}
						}
					}
				}
			}
		}
	}
}

.path-pay-info {
	.path-item {
		&:nth-of-type(3) {
			svg {
				color: #00b371;
			}
		}
		&:nth-of-type(5) {
			svg {
				color: $primaryColor;
			}
		}
	}
}
