.content {
	position: relative;
}
.top-slider {
	height: 480px;
	width: 100%;
	position: relative;
	&-item {
		width: 100%;
		height: 480px;
	}
	&-content {
		position: absolute;
		top: 150px;
		left: 50%;
		transform: translate(-50%, 0%);
		min-width: 1160px;
		z-index: 5;
		color: $textWhite;

		h1 {
			font-size: 60px;
			line-height: 1.18;
			font-weight: 600;
			min-width: 750px;
			margin-bottom: 2px;
		}

		p {
			margin: 0 0 20px;
			font-size: 20px;
			line-height: 30px;
			font-weight: 400;
		}

		.slider-search-form {
			display: flex;
			align-items: center;
			position: relative;
      top: 100%;
			width: 800px;
			height: 56px;
			padding: 0 3px 0 16px;
			box-sizing: border-box;
			background: #fff;
			border-radius: 8px;
			z-index: 5;
			border: 1px solid transparent;
			color: #212121;

			&:hover {
				border: 1px solid $primaryColor;
			}

			&-focus {
				border: 1px solid $primaryColor;
			}

			form {
				display: flex;
				align-content: center;
				justify-content: center;
				width: 100%;

				.search-form-icon {
					flex: 0 0 24px;
					width: 425px;
					margin-right: 8px;
					display: flex;
					align-items: center;
					justify-content: center;

					i {
						font-size: 28px;
						color: $textBlack;
					}
				}

				.search-form-input {
					flex: 1;
					position: relative;
					width: 100%;

					input {
						line-height: 1.44;
						font-weight: 400;
						box-sizing: border-box;
						overflow: hidden;
						outline: none;
						width: 100%;
						padding: 15px 0;
						border: 1px solid #fff;
						border-radius: 4px;
						background-color: transparent;
						position: relative;
						z-index: 10;
						font-size: 16px;
						color: #212121;
						white-space: nowrap;
						text-overflow: ellipsis;
						height: 54px;
						caret-color: $textOrange;
					}
				}

				.search-form-button {
					display: -webkit-box;
					display: flex;
					-webkit-box-align: center;
					align-items: center;
					-webkit-box-flex: 0;
					flex: none;

					button {
						padding: 0 40px;
						border-radius: 6px;
						margin-left: 16px;
						background: $primaryColor;
						color: #fff;
						height: 48px;
						border: none;
						outline: none;
						cursor: pointer;
						font-size: 16px;
						line-height: 1.44;
						font-weight: 500;
					}
				}
			}
		}
