+            transform: translate(-50%, -50%);
+            min-width: 1160px;
+            z-index: 5;
+            color: $textWhite;
+
+            h1 {
+                font-size: 60px;
+                line-height: 1.18;
+                font-weight: 600;
+                min-width: 750px;
+                margin-bottom: 2px;
+            }
+
+            p {
+                margin: 0 0 20px;
+                font-size: 20px;
+                line-height: 30px;
+                font-weight: 400;
+            }
+
+            .slider-search-form {
+                display: flex;
+                -webkit-box-align: center;
+                align-items: center;
+                position: relative;
+                width: 800px;
+                height: 56px;
+                padding: 0 3px 0 16px;
+                box-sizing: border-box;
+                background: #fff;
+                border-radius: 8px;
+                z-index: 5;
+                border: 1px solid transparent;
+                color: #212121;
+
+                &:hover {
+                    border: 1px solid $primaryColor;
+                }
+
+                &-focus {
+                    border: 1px solid $primaryColor;
+                }
+
+                form {
+                    display: flex;
+                    align-content: center;
+                    justify-content: center;
+                    width: 100%;
+
+                    .search-form-icon {
+                        flex: 0 0 24px;
+                        width: 24px;
+                        margin-right: 8px;
+                        display: flex;
+                        align-items: center;
+                        justify-content: center;
+
+                        i {
+                            font-size: 28px;
+                            color: $textBlack;
+                        }
+                    }
+
+                    .search-form-input {
+                        flex: 1;
+                        position: relative;
+                        width: 100%;
+
+                        input {
+                            line-height: 1.44;
+                            font-weight: 400;
+                            box-sizing: border-box;
+                            overflow: hidden;
+                            outline: none;
+                            width: 100%;
+                            padding: 15px 0;
+                            border: 1px solid #fff;
+                            border-radius: 4px;
+                            background-color: transparent;
+                            position: relative;
+                            z-index: 10;
+                            font-size: 16px;
+                            color: #212121;
+                            white-space: nowrap;
+                            text-overflow: ellipsis;
+                            height: 54px;
+                            caret-color: $textOrange;
+                        }
+                    }
+
+                    .search-form-button {
+                        display: -webkit-box;
+                        display: flex;
+                        -webkit-box-align: center;
+                        align-items: center;
+                        -webkit-box-flex: 0;
+                        flex: none;
+
+                        button {
+                            padding: 0 40px;
+                            border-radius: 6px;
+                            margin-left: 16px;
+                            background: $primaryColor;
+                            color: #fff;
+                            height: 48px;
+                            border: none;
+                            outline: none;
+                            cursor: pointer;
+                            font-size: 16px;
+                            line-height: 1.44;
+                            font-weight: 500;
+                        }
+                    }
+
+                }
+
+
+            }
+        }
+
+        .slider-search-form-dropdown {
+            display: none;
+            position: absolute;
+            top: calc(128% + 0px);
+            left: calc(50% - 178px);
+            -webkit-transform: translate(-50%, -50%);
+            transform: translate(-50%, -50%);
+            width: 800px;
+            background-color: $textWhite;
+            border-radius: 8px;
+            -webkit-box-shadow: 0 6px 20px rgb(0 0 0 / 8%);
+            box-shadow: 0 6px 20px rgb(0 0 0 / 8%);
+            overflow: hidden;
+            z-index: 10;
+
+            &-container {
+                position: relative;
+                padding: 28px 20px 32px;
+                min-height: 200px;
+                max-height: 540px;
+                overflow-y: auto;
+                -ms-scroll-chaining: none;
+                overscroll-behavior: contain;
+            }
+
+            &-top {
+                margin-bottom: 20px;
+
+                &-title {
+                    display: -webkit-box;
+                    display: flex;
+                    -webkit-box-pack: justify;
+                    justify-content: space-between;
+                    margin-bottom: 7px;
+
+                    h3 {
+                        font-size: 16px;
+                        line-height: 1.44;
+                        font-weight: 600;
+                    }
+                }
+
+                &-list {
+                    display: -webkit-box;
+                    display: flex;
+                    -webkit-box-orient: vertical;
+
+                    span {
+                        font-size: 14px;
+                        line-height: 1.44;
+                        font-weight: 400;
+                        display: inline-block;
+                        padding: 8px 10px;
+                        background: #f5f5f5;
+                        border-radius: 99rem;
+                        margin: 5px 10px 5px 0;
+                        max-width: 310px;
+                        height: 36px;
+                        overflow: hidden;
+                        text-overflow: ellipsis;
+                        white-space: nowrap;
+                        cursor: pointer;
+
+                        &:hover {
+                            background: #eee;
+                        }
+                    }
+                }
+            }
+
+            &-bottom {
+                display: flex;
+                -webkit-box-pack: justify;
+                justify-content: space-between;
+
+                &-col {
+                    width: 364px;
+                    height: 100%;
+                    padding: 20px 12px;
+                    border-radius: 8px;
+                    border: 1px solid #eee;
+                    background-size: contain;
+                    background-repeat: no-repeat;
+
+                    h3 {
+                        font-size: 14px;
+                        line-height: 1.44;
+                        font-weight: 600;
+                        position: relative;
+                        max-width: 320px;
+                        overflow: hidden;
+                        text-overflow: ellipsis;
+                        white-space: normal;
+                        display: -webkit-box;
+                        -webkit-box-orient: vertical;
+                        -webkit-line-clamp: 1;
+                        z-index: 2;
+                        color: #212121;
+                        padding: 0 2px;
+                    }
+
+                    &-list {
+                        &-item {
+                            text-decoration: none;
+                            margin-top: 16px;
+                            display: flex;
+
+                            &-left {
+                                width: 54px;
+                                height: 54px;
+                                border-radius: 6px;
+                                background-size: cover;
+                                background-position: 50%;
+                                background-repeat: no-repeat;
+                                overflow: hidden;
+
+                                &-top {
+                                    background-size: contain;
+                                    background-repeat: no-repeat;
+                                    text-align: center;
+                                    font-size: 12px;
+                                    line-height: 1.44;
+                                    font-weight: 500;
+                                    width: 16px;
+                                    height: 21px;
+                                    color: #fff;
+
+                                }
+                            }
+
+                            &-left-48 {
+                                width: 48px;
+                                height: 48px;
+                            }
+
+                            &-right {
+                                width: 100%;
+                                margin: -2px 0 0 12px;
+
+                                h4 {
+                                    font-size: 12px;
+                                    line-height: 1.44;
+                                    font-weight: 500;
+                                    overflow: hidden;
+                                    text-overflow: ellipsis;
+                                    white-space: normal;
+                                    display: -webkit-box;
+                                    -webkit-box-orient: vertical;
+                                    -webkit-line-clamp: 2;
+                                    color: #212121;
+                                }
+
+                                &-desc {
+                                    display: -webkit-box;
+                                    display: flex;
+                                    flex-wrap: nowrap;
+                                    margin-top: 4px;
+
+                                    p {
+                                        font-size: 12px;
+                                        line-height: 1.44;
+                                        font-weight: 400;
+                                        overflow: hidden;
+                                        text-overflow: ellipsis;
+                                        white-space: normal;
+                                        display: -webkit-box;
+                                        -webkit-box-orient: vertical;
+                                        -webkit-line-clamp: 1;
+                                        color: #999;
+                                        -webkit-box-flex: 1;
+                                        flex: 1;
+                                    }
+
+                                    &-price {
+                                        font-size: 12px;
+                                        line-height: 1.44;
+                                        font-weight: 400;
+                                        color: $textOrange;
+                                        margin-left: 8px;
+
+                                        strong {
+                                            font-size: 12px;
+                                        }
+                                    }
+                                }
+                            }
+                        }
+                    }
+                }
+            }
+        }
 	}
 }

